mapper.select:
SELECT * FROM (
   SELECT A.*, ROWNUM rnum FROM (
     SELECT * FROM APPROVAL

       WHERE ${searchField} LIKE '%'||#{searchKeyword}||'%'

     ORDER BY CREATED_AT DESC
   ) A
   WHERE ROWNUM <= #{end}
)
WHERE rnum >= #{start}

mapper.update:
UPDATE APPROVAL
   SET TITLE = #{title},
       CONTENT = #{content},
       CATEGORY = #{category}
 WHERE APPROVAL_NO = #{approvalNo}
